mapper.select:
SELECT menuId, parentId, menuName, href, icon FROM ( SELECT DISTINCT m.menu_id AS menuId, parent_id AS parentId, menu_name AS menuName, href, icon FROM cms_menu m LEFT JOIN cms_role_menu r ON r.menu_id = m.menu_id WHERE status = #{status} AND menu_type = #{menuType} AND r.role_id in
#{roleId}
ORDER BY sort ASC ) AS t;
