mapper.select:
SELECT p.product_id AS productId, product_number AS productNumber, name, show_price AS showPrice, pic_img AS picImg, introduce, p.label_id AS labelId, label_name AS labelName FROM os_product p LEFT JOIN os_product_category c ON p.product_id = c.product_id LEFT JOIN os_label l ON p.label_id = l.label_id WHERE p.status = #{status} AND c.category_id IN
#{categoryId}
ORDER BY RAND() LIMIT #{showNumber}

SELECT p.product_id AS productId, product_number AS productNumber, name, show_price AS showPrice, pic_img AS picImg, introduce, p.label_id AS labelId, a.comment_number AS commentNumber, label_name AS labelName FROM os_product p LEFT JOIN os_product_category c ON c.product_id = p.product_id LEFT JOIN os_label l ON l.label_id = p.label_id LEFT JOIN os_product_attribute a ON a.product_id = p.product_id WHERE p.status = #{status} AND c.category_id IN
#{categoryId}
ORDER BY a.sales_volume DESC LIMIT #{showNumber}
